package com.java;

public class SwappingTwoValues {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 20;
		int b = 10;
		int c = 0;
//swapping two values by creating third variable c=0
		c = a;// c=20
		a = b;// a=10
		b = c;// b=20

		System.out.println(a);
		System.out.println(b);
	}

}
